module.exports = app => {
  app.sessionCache = {
    async getSessionById(sessionId) {
      const ctx = app.currentContext;
      const traceId = ctx && ctx.traceId;
      console.log('[session.cache] getSessionById %s, traceId: %s', sessionId, traceId);
      return {
        sessionId,
        traceId,
      };
    },
  };
};
