module.exports = app => {
  app.sessionCache = {
    async getSessionById(sessionId) {
      const ctx = app.currentContext;
      console.log('[session.cache] getSessionById %s, traceId: %s', sessionId, ctx.traceId);
      return {
        sessionId,
        traceId: ctx.traceId,
      };
    },
  };
};
